            if (item.length() < MAX_ITEMS) {
                String processedItem = processItem(item);
                outputData.add(processedItem);
            }
        }
        return outputData;
    }

    private String processItem(String item) {
        String cleanedItem = item.trim();
        cleanedItem = cleanedItem.replaceAll("\\s+", " ");
        return cleanedItem;
